$('#closeicon').click(function () {
    setTimeout(function () {
        $('#closeicon').find('i').toggleClass('fa-caret-right fa-times-circle')
        if ($('#Cmenu_circle').css('opacity') == 1) {
            $('#menuOverlay').removeAttr("style");
            $(".page-container").css({
                'z-index': ''
            });
            $('#Cmenu_circle').css({
                '-webkit-transform': 'scale(0)',
                '-moz-transform': 'scale(0)',
                '-ms-transform': 'scale(0)',
                '-o-transform': 'scale(0)',
                'transform': 'scale(0)'
            });
            $('#Cmenu_circle').css('opacity', '0')
        } else {
            $('#menuOverlay').css({
                'background': '#00000096',
                'display': 'block',
                'height': '100%',
                'width': '100%'
            });
            $(".page-container").css({
                'z-index': '-1'
            });
            $('#Cmenu_circle').css({
                '-webkit-transform': 'scale(1)',
                '-moz-transform': 'scale(1)',
                '-ms-transform': 'scale(1)',
                '-o-transform': 'scale(1)',
                'transform': 'scale(1)'
            });
            $('#Cmenu_circle').css('opacity', '1')
        }
    }, 200);
});

