$('#closeicon_left').click(function () {
    setTimeout(function () {
        if($('#closeicon_left').is(":checked") == true){
            $('#close_icon_left').find('i').toggleClass('fa-caret-right fa-times-circle')
            $('#menuOverlay').css({
                'background': 'rgba(0, 0, 0, 0.70)',
                'display': 'block',
                'height': '100%',
                'width': '100%'
            });
            $(".page-container").css({
                'z-index': '-1'
            });
        }else{
            $('#close_icon_left').find('i').toggleClass('fa-times-circle fa-caret-right')
            $('#menuOverlay').removeAttr("style");
            $(".page-container").removeAttr("style");
        }
    }, 200);
});

$('#menuOverlay').click(function () {
    setTimeout(function () {
        if($('#closeicon_left').is(":checked") == true){
            $('#closeicon_left').prop('checked', false);
            $('#close_icon_left').find('i').toggleClass('fa-times-circle fa-caret-right')
            $('#menuOverlay').removeAttr("style");
            $(".page-container").removeAttr("style");
        } else {
            //console.log('Not Show')
        }
    }, 300);
});


